function initModal(){const t=document.querySelectorAll(".js-modal");document.querySelectorAll(".js-btn-modal").forEach((e=>{e.addEventListener("click",(()=>{t.forEach((t=>{e.getAttribute("data-modal")==t.getAttribute("data-modal")&&t.classList.add("is-modal-show")}))}))}))}function closeModal(){document.querySelectorAll(".js-close-modal").forEach((t=>{t.addEventListener("click",(t=>{console.log(t.target.parentElement);let e=t.target.parentElement;e.classList.contains("is-modal-show")&&e.classList.remove("is-modal-show")}))}))}initModal(),closeModal();